class RanSize{constructor(t,s={}){this.controls={min:s.min||1,max:s.max||2,colors:s.colors||!1,pattern:s.pattern||!1,value:s.value||[1,1.2,1.4,1.6,1.8,2],class:s.class||"",rotate:s.rotate||!1},this.i=-1,this.id=t,this.final="",this.randRGB=(()=>Math.floor(255*Math.random())+1),this.randomSize=((t,s)=>Math.random()*(t-s)+s),this.ranCol=(()=>`rgb(${this.randRGB()}, ${this.randRGB()}, ${this.randRGB()})`),this.coolPattern=(()=>(this.i===this.controls.value.length-1?this.i=0:this.i+=1,this.controls.value[this.i])),this.rotateText=(()=>this.randomSize(10,-10))}get getText(){return document.querySelector("#"+this.id).innerText}apply(){this.final="",this.getText.split("").forEach(t=>{this.final+=`<span class = "${this.controls.class}" style = "\n                font-size: ${!0===this.controls.pattern?this.coolPattern()+"em":this.randomSize(this.controls.max,this.controls.min)+"em"};${!0===this.controls.colors?`\n                color: ${this.ranCol()};`:""}${!0===this.controls.rotate?`\n                display: inline-block;\n                transform: rotate(${this.rotateText()+"deg"})`:""}\n            ">${t}</span>`}),document.querySelector("#"+this.id).innerHTML=this.final}}