class RanSize{constructor(t,o={}){this.controls={min:o.min||1,max:o.max||2,colors:o.colors||!1,pattern:o.pattern||!1,value:o.value||[1,1.2,1.4,1.6,1.8,2],class:o.class||"",rotate:o.rotate||!1},this.i=-1,this.id=t,this.final="",this.randomSize=((t,o)=>Math.random()*(t-o)+o),this.ranCol=(()=>`rgb( ${Math.floor(255*Math.random())+1} ,${Math.floor(255*Math.random())+1} ,${Math.floor(255*Math.random())+1})`),this.coolPattern=(()=>(this.i===this.controls.value.length-1?this.i=0:this.i+=1,this.controls.value[this.i])),this.rotateText=(()=>20*Math.random()-10)}get getText(){return document.querySelector("#"+this.id).innerText}apply(){this.final="",this.getText.split("").forEach(t=>{this.final+=`<span class = "${this.controls.class}" style = "font-size: ${!0===this.controls.pattern?this.coolPattern()+"em":this.randomSize(this.controls.max,this.controls.min)+"em"};${!0===this.controls.colors?`color: ${this.ranCol()};`:""}${!0===this.controls.rotate?`display: inline-block;                transform: rotate(${this.rotateText()+"deg"})`:""}">${t}</span>`}),document.querySelector("#"+this.id).innerHTML=this.final}}