class RanSize{constructor(t,s={}){this.controls={min:s.min||1,max:s.max||2,colors:s.colors||!1,pattern:s.pattern||!1,value:s.value||[1,1.2,1.4,1.6,1.8,2],class:s.class||""},this.i=-1,this.id=t,this.final="",this.randomSize=((t,s)=>Math.random()*(t-s)+s),this.ranCol=(()=>!0===this.controls.colors?`rgb( ${Math.floor(255*Math.random())+1} ,${Math.floor(255*Math.random())+1} ,${Math.floor(255*Math.random())+1})`:"currentColor"),this.coolPattern=(()=>(this.i===this.controls.value.length-1?this.i=0:this.i+=1,this.controls.value[this.i]))}get getText(){return document.querySelector("#"+this.id).innerText}apply(){this.final="",this.getText.split("").forEach(t=>{this.final+=`<span class = "${this.controls.class}" style = "font-size: ${!0===this.controls.pattern?this.coolPattern()+"em":this.randomSize(this.controls.max,this.controls.min)+"em"};color: ${this.ranCol()};">${t}</span>`}),document.querySelector("#"+this.id).innerHTML=this.final}}